INSERT INTO str_wordpress.wp_posts (ID, post_author, post_date, post_date_gmt, post_content, post_title, post_excerpt, post_status, comment_status, ping_status, post_password, post_name, to_ping, pinged, post_modified, post_modified_gmt, post_content_filtered, post_parent, guid, menu_order, post_type, post_mime_type, comment_count) VALUES (60, 1, '2018-03-23 17:24:18', '2018-03-23 17:24:18', 'Strategy to Revenue’s Pathfinder assessment identified the influence of the company’s culture on the team, their motivations, reservations, and working styles. From here, a bespoke Pathfinder program was developed to coach the team how to adopt behaviors that would help the company achieve its goals. Through a Compass SKILLbuild™ online assessment, individuals identified their own performance shortfalls. This formed the basis of a nine-week individual learning path with links to learning, work-based activities and exercises, followed by a two-day workshop where newly learnt competencies were reinforced. The company implemented Strategy to Revenue’s recommendations on how to compensate behaviors using metrics and language associated with customer success rather than sales. Upon completion of the program, team managers were given the tools to continue on-the-job coaching and assessment, ensuring new skills weren’t lost and standards were maintained.', 'Strategy to Revenue Drives 20% Increase In Customer Spend', '', 'publish', 'closed', 'closed', '', 'strategy-to-revenue-drives-20-increase-in-customer-spend', '', '', '2018-03-23 17:28:12', '2018-03-23 17:28:12', '', 0, 'http://localhost:8888/?post_type=str_success_story&#038;p=60', 0, 'str_success_story', '', 0);
