INSERT INTO str_wordpress.wp_posts (ID, post_author, post_date, post_date_gmt, post_content, post_title, post_excerpt, post_status, comment_status, ping_status, post_password, post_name, to_ping, pinged, post_modified, post_modified_gmt, post_content_filtered, post_parent, guid, menu_order, post_type, post_mime_type, comment_count) VALUES (63, 1, '2018-03-23 15:38:09', '2018-03-23 15:38:09', 'Strategy to Revenue’s Compass SKILLBuild™ platform was used to assess each sales person’s competencies; the answers provided an opportunity for the sales person to discuss and agree desired final results with their line manager. Based on an agreed competency score, a self-paced, personalized learning pathway was developed, thereby enabling people to demonstrate their understanding of key competencies before undertaking the module. This reduced the time spent by salespeople on unnecessary training and ensured their maximum engagement in the modules. The customizable nature of the Compass SKILLBuild™ platform reflected the language, terminology and market requirements of this very specific field of business.', 'Setting Global Standard for Online Learning', '', 'publish', 'closed', 'closed', '', 'setting-global-standard-for-online-learning', '', '', '2018-03-26 16:07:45', '2018-03-26 15:07:45', '', 0, 'http://localhost:8888/?post_type=str_success_story&#038;p=63', 0, 'str_success_story', '', 0);
