INSERT INTO str_wordpress.wp_posts (ID, post_author, post_date, post_date_gmt, post_content, post_title, post_excerpt, post_status, comment_status, ping_status, post_password, post_name, to_ping, pinged, post_modified, post_modified_gmt, post_content_filtered, post_parent, guid, menu_order, post_type, post_mime_type, comment_count) VALUES (72, 1, '2018-03-26 15:56:28', '2018-03-26 14:56:28', 'The mobile operator approached Strategy to Revenue to build a program to give sales managers the necessary skills to deliver on-the- job coaching to their teams. The Pathfinder process identified the company culture and common personality traits amongst sales managers which were shaping the behavior of the sales team. From here, an international standard for sales coaching competency was established. A Compass SKILLBuild™ assessment created a bespoke, self-paced learning path for each sales manager which included a mix of online learning and workplace activity. Successful completion of the course ensured a consistent base level of competency, which was then enhanced via a two-day intensive workshop, affording sales managers the opportunity to discuss their learning and share examples of best practice.', 'Global Operator Rings In Improved Sales Behaviors', '', 'publish', 'closed', 'closed', '', 'global-operator-rings-in-improved-sales-behaviors', '', '', '2018-03-26 15:57:25', '2018-03-26 14:57:25', '', 0, 'http://localhost:8888/?post_type=str_success_story&#038;p=72', 0, 'str_success_story', '', 0);
