INSERT INTO str_wordpress.wp_posts (ID, post_author, post_date, post_date_gmt, post_content, post_title, post_excerpt, post_status, comment_status, ping_status, post_password, post_name, to_ping, pinged, post_modified, post_modified_gmt, post_content_filtered, post_parent, guid, menu_order, post_type, post_mime_type, comment_count) VALUES (75, 1, '2018-03-26 16:11:30', '2018-03-26 15:11:30', 'To enable DHL Global Forwarding’s sales team to cross sell Ocean Freight products to new and existing customers, Strategy to Revenue initiated a short but intense consultation followed by a brainstorm with 20 DHL Global Forwarding DGF subject matter experts; this provided insight into target customers’ decision-making issues. Strategy to Revenue created a value proposition mapping and messaging strategy along with supporting sales tools. In a market where differentiation is difficult the DHL Global Forwarding DGF campaign armed the sales team with the tools to demonstrate the strength of their proposition and improve their product knowledge to sell more. Coaching sessions in multiple languages, including Chinese, were rolled out to 3,400 sales people and supported with online sales tools. Global alignment and consistency of message were ensured through webinars, and weekly communications and inter-country leagues kept the campaign top-of-mind.', 'DHL Delivers 20%+ Pipeline Increase', '', 'publish', 'closed', 'closed', '', 'dhl-delivers-20-pipeline-increase', '', '', '2018-03-26 16:11:30', '2018-03-26 15:11:30', '', 0, 'http://localhost:8888/?post_type=str_success_story&#038;p=75', 0, 'str_success_story', '', 0);
